import { Component } from '@angular/core';
import { Login } from 'src/app/interfaces/login';
import { GlopalService } from 'src/app/services/glopal.service';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  model: Login = {
    email: '',
    password: '',
  };
  constructor(private glopal: GlopalService) {}
  handleSubmit(form: NgForm) {
    if (form.valid) {
      this.glopal.login(this.model).subscribe(
        (res) => {
          console.log(res);
        },
        (err) => {
          console.log(err);
        }
      );
    }
  }
}
<form class="row gy-4" #loginForm="ngForm" (ngSubmit)="handleSubmit(loginForm)">
  <div class="col-12 col-md-6 col-lg-4">
    <input
      type="email"
      class="form-control"
      placeholder="email"
      name="email"
      #email="ngModel"
      [(ngModel)]="model.email"
    />
  </div>

  <div class="col-12 col-md-6 col-lg-4">
    <input
      type="password"
      class="form-control"
      placeholder="password"
      name="password"
      #email="ngModel"
      [(ngModel)]="model.password"
    />
  </div>
  <div class="col-6">
    <button class="btn btn-primary">Login</button>
  </div>
</form>
